<script>
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

export default {
  mounted() {
    gsap.registerPlugin(ScrollTrigger);

    let tl = gsap.timeline({
      scrollTrigger: {
        trigger: '.about',
        toggleActions: 'restart none none reset',
      },
    });

    tl.from('.card', {
      stagger: 0.5,
      opacity: 0,
      duration: 0.5,
    })
      .from(
        '.card__title',
        {
          stagger: 0.5,
          opacity: 0,
          duration: 0.5,
        },
        '-=1'
      )
      .from(
        '.card__text',
        {
          stagger: 0.5,
          opacity: 0,
          y: '-3rem',
          duration: 0.5,
        },
        '-=1'
      )
      .from(
        '.card__icon',
        {
          stagger: 0.5,
          opacity: 0,
          y: '3rem',
          duration: 0.5,
        },
        '-=1.5'
      );
  },
};
</script>

